name:
fun PilotRichText(richText: PilotRichText, modifier: Modifier = Modifier, color: Color = Color.Unspecified, fontSize: TextUnit = TextUnit.Unspecified, fontStyle: FontStyle? = null, fontWeight: FontWeight? = null, fontFamily: FontFamily? = null, letterSpacing: TextUnit = TextUnit.Unspecified, textDecoration: TextDecoration? = null, textAlign: TextAlign? = null, lineHeight: TextUnit = TextUnit.Unspecified, overflow: TextOverflow = TextOverflow.Clip, softWrap: Boolean = true, maxLines: Int = Int.MAX_VALUE, minLines: Int = 1, onTextLayout: (TextLayoutResult) -> Unit = {}, style: TextStyle = LocalTextStyle.current, inlineTextContent: (PilotRichText.Content.Image) -> InlineTextContent = { image ->
        val placeholderSize = fontSize.takeOrElse { style.fontSize }.takeOrElse { 14.sp }
        defaultImageInlineTextContent(
            image = image,
            placeholder = Placeholder(
                // 1.5f gives us a little extra horizontal padding by default
                width = placeholderSize * 1.5f,
                height = placeholderSize,
                placeholderVerticalAlign = PlaceholderVerticalAlign.Center,
            ),
        )
    })

